const { GoogleGenerativeAI } = require("@google/generative-ai");
const fs = require("fs");
const path = require("path");
const { execSync } = require('child_process');
const ffmpegPath = require('ffmpeg-static');

require('dotenv').config();

const API_KEY = process.env.GEMINI_API_KEY;
const genAI = new GoogleGenerativeAI(API_KEY);
const model = genAI.getGenerativeModel({ model: "gemini-2.5-flash" });

async function run() {
    let filePath = "skills/sticker-analyzer/temp_analyze.gif";

    if (!fs.existsSync(filePath)) {
        console.log("File not found");
        return;
    }

    console.log(`Converting GIF to WebP...`);
    const webpPath = filePath.replace(/\.gif$/i, '.webp');
    try {
        execSync(`${ffmpegPath} -i "${filePath}" -c:v libwebp -lossless 0 -q:v 75 -loop 0 -an -vsync 0 -y "${webpPath}"`, { stdio: 'pipe' });
        filePath = webpPath;
    } catch (e) {
        console.error("Conversion failed:", e.message);
        return;
    }

    console.log(`Analyzing...`);
    const imagePart = {
        inlineData: {
            data: fs.readFileSync(filePath).toString("base64"),
            mimeType: "image/webp"
        }
    };

    const prompt = `Describe this image in detail. Is it a sticker? If so, what is the character doing? Response in plain text.`;
    const result = await model.generateContent([prompt, imagePart]);
    console.log(result.response.text());
}

run();

